import React, {useState} from 'react';
import TopNavSimpleLogin from '../components/Navbar/TopNavSimpleLogin';
import { useNavigate } from 'react-router-dom';

const Login = () => {
    const history = useNavigate();
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [errorMessage, setErrorMessage] = useState('');
    function handleClick() {
      history('/sign-up');
    }
    function loginButton() {
        history('/main');

        localStorage.setItem('isLoggedIn', true);
      }
    return (
	<div className='full_div'>
      <TopNavSimpleLogin />

      <center><h1>Login to your MarketPlants account!</h1></center>
      <center>
            <div style={{ border: '2px solid green', padding: '20px', borderRadius: '10px', boxSizing: 'border-box', marginLeft: '40%' , marginRight: '40%'}}>
                <div>
                    <h3 style={{marginBottom: 0}}>Email</h3>
                    <input type="text" style={{ backgroundColor: '#f2f2f2', borderRadius: '5px', border: 'none', padding: '10px', marginBottom: '10px' }} />
                </div>
                <div>
                    <h3 style={{marginBottom: 0}}>Password</h3>
                    <input type="password" style={{ backgroundColor: '#f2f2f2', borderRadius: '5px', border: 'none', padding: '10px', marginBottom: '10px' }} />
                </div>
                <div>
                    <button style={{ backgroundColor: '#6B8E23', color: 'white', padding: '10px 20px', fontSize: '20px', borderRadius: '5px', border: 'none', cursor: 'pointer' }} onClick={loginButton} >Login</button>

                </div>
                <div>
                    <h2 style={{marginTop:20, marginBottom:0}}>Forgot your password?</h2>
                    <button style={{ marginTop:'5px',backgroundColor: '#6B8E23', color: 'white', padding: '5px 20px', fontSize: '20px', borderRadius: '5px', border: 'none', cursor: 'pointer' }}>Recover password</button>
                </div>
            </div>
            <div>
                    <h2 style={{marginTop:20}}>Or Register if you don't have an account: <button style={{ backgroundColor: '#6B8E23', color: 'white', padding: '5px 20px', fontSize: '20px', borderRadius: '5px', border: 'none', cursor: 'pointer' }}   onClick={handleClick}>Register</button></h2>
            </div>
      </center>
      <div className='bottom_panel'>
      </div>
    </div>
);
};

export default Login;
