import React, { useState } from "react";
import TopNav from '../components/Navbar/TopNav';
//import { useNavigate } from 'react-router-dom';
const Main = () => {

	const [showSubcategories, setShowSubcategories] = useState(false);

  const toggleSubcategories = () => {
    setShowSubcategories(!showSubcategories);
  };
return (
	<div className='full_div'>
		<TopNav />
		<div style={{ display: 'flex', justifyContent: 'flex-end' }}>
			<input 
  			type="text" 
  			placeholder="Search products..."
  			style={{
  			  backgroundColor: '#ccc',
  			  borderRadius: '20px',
  			  padding: '10px',
  			  border: 'none',
  			  width: '200px',
			  height: '50px',
  			  textAlign: 'left',
  			  marginBottom: '10px'	,
			  marginTop:'10px',
			  marginRight: '30px'
  			}}
		/>
		</div>
		<div style={{display: 'flex'}}>
			<div style={{ width: "20%", border: "5px solid #6B8E23" }}>
    		  <h2 className="plants-to-show">Categories:</h2>
    		  <button onClick={toggleSubcategories}>All Plants</button>
    		  {showSubcategories && (
    		    <div>
    		      <button>Indoor Plants</button>
    		      <button>Outdoor Plants</button>
    		    </div>
    		  )}
    		</div>
			<div style={{flex: 1,border: '5px solid #6B8E23'}}>
				<h2 className="plants-to-show">Plants to show:</h2>
			</div>
		</div>

    </div>
);
};

export default Main;
