import React, {useState} from 'react';
import TopNav from '../components/Navbar/TopNav';
import pedro from "../images/MariaFloristaLogo.jpg";
import ShowPlants from '../components/ShowPlants';




const Profile = () =>{

    const [comments, setComments] = useState([]);
    const [newComment, setNewComment] = useState('');

    function handleSubmit(e) {
      e.preventDefault();
      if (newComment.trim() === '') return;
      setComments([...comments, newComment.trim()]);
      setNewComment('');
    }

    const [rating, setRating] = useState(0);

    const handleRating = (value) => {
        setRating(value);
    }

    return(
        <div className='full_div'>
            <TopNav />
            <div style={{paddingLeft:'100px',marginTop:'50px'}}></div>
            <center>
            <div style={{ border: '2px solid green',padding: '20px', borderRadius: '10px', boxSizing: 'border-box', width: '80%' }}>
                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', width: '100%', margin: '0 auto' }}>

                    <div><img src={logo} alt="Maria Florista" style={{ width: 'auto', height: '60%', objectFit: 'cover' }} /></div>
                    <div style={{ border: '2px solid green',padding: '20px', borderRadius: '10px', boxSizing: 'border-box', width: '60%' }}>   
                        <div><h3>Name : Pedro Guerra</h3></div>
                        <div><h3>Location : Porto, Portugal</h3></div>
                    </div>
                </div>
                    <div style={{ marginTop:'10px'}}><h2>Store best sellers:</h2></div>
                    <ShowPlants></ShowPlants>
                <div>
                  <h2>Comments</h2>
                    <form onSubmit={handleSubmit}>
                        <input
                            type="text"
                            placeholder="Add a comment"
                            value={newComment}
                            onChange={(e) => setNewComment(e.target.value)}
                            />
                        <button type="submit">Submit</button>
                    </form>
                    <ul>
                    {comments.map((comment, index) => (
                      <li key={index}>{comment}</li>
                    ))}
                  </ul>
                </div>
            </div>
            </center>
        </div>
    );
}

export default Profile;
