import React, { useState } from 'react';
import TopNav from '../components/Navbar/TopNav';
import { useNavigate } from 'react-router-dom';

const AddPlant = () => {
  const [plantData, setPlantData] = useState({
    photo: '',
    name: '',
    scientificName: '',
    description: '',
    price: '',
    shipping: '',
    lightRequirements: '',
    watering: '',
    temperature: '',
    humidity: '',
    growthRate: '',
    flower: '',
    fruit: '',
    animalFriendly: '',
    extraCautions: '',
  });

  const history = useNavigate();

  function cancel() {
    history('/profile');
  }

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    if (type === 'checkbox') {
      setPlantData((prevState) => ({
        ...prevState,
        [name]: checked ? 'Yes' : 'No',
      }));
    } else {
      setPlantData((prevState) => ({
        ...prevState,
        [name]: value,
      }));
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(plantData);

    // TODO: Create a new plant object using plantData
    const newPlant = { ...plantData };

    // Get the existing user's array of plants from local storage
    const userPlants = JSON.parse(localStorage.getItem('userPlants')) || [];

    // Update the user's array with the new plant
    const newUserPlants = [...userPlants, newPlant];

    // Save the updated user's array of plants to local storage
    localStorage.setItem('userPlants', JSON.stringify(newUserPlants));

    // Clear the form after saving the plant
    setPlantData({
      photo: '',
      name: '',
      scientificName: '',
      description: '',
      price: '',
      shipping: '',
      lightRequirements: '',
      watering: '',
      temperature: '',
      humidity: '',
      growthRate: '',
      flower: '',
      fruit: '',
      animalFriendly: '',
      extraCautions: '',
    });
  };

  return (
    <div className="full_div2">
      <TopNav />
      <center>
        <div style={{ paddingLeft: '100px', marginTop: '50px' }}></div>
        <div style={{ border: '2px solid green', padding: '20px', borderRadius: '10px', boxSizing: 'border-box', width: '80%' }}>
          <form onSubmit={handleSubmit}>
            {/* Rest of the form */}
          </form>
          <button type="submit" style={{ marginRight: '10px', backgroundColor: '#6B8E23', color: 'white', padding: '5px 20px', fontSize: '20px', borderRadius: '10px', border: 'none', cursor: 'pointer', marginTop: '5px' }}>Add Plant</button>
        </div>
      </center>
      <div style={{ marginTop: '50px', width: '100%', height: '100px', backgroundColor: '#6B8E23' }}></div>
    </div>
  );
}

export default AddPlant;
