import React, { useState } from "react";

function Categories() {
  const [showSubcategories, setShowSubcategories] = useState(false);

  const toggleSubcategories = () => {
    setShowSubcategories(!showSubcategories);
  };

  return (
    <div style={{ width: "20%", border: "5px solid #6B8E23", paddingLeft: "5px" }}>
  <h2 className="plants-to-show">Categories:</h2>
  <button style={{ border: 'none', paddingLeft: '0', fontWeight: 'bold' }} onClick={toggleSubcategories}>
    <h3 style={{ display: 'inline-block', marginRight: '10px' }}>All Plants</h3>
    {showSubcategories ? <span>&#x25BC;</span> : <span>&#x25B6;</span>}
  </button>
  {showSubcategories && (
  <div style={{ display: 'flex', flexDirection: 'column', paddingLeft: '10px' }}>
    <button style={{ border: 'none', fontWeight: 'bold',marginTop:1 }}>Indoor Plants</button>
    <button style={{ border: 'none', fontWeight: 'bold',marginTop:1 }}>Outdoor Plants</button>
  </div>
  )}
</div>

  );
}

export default Categories;
