import React from "react";
import TopNav from '../components/Navbar/TopNav';
import Categories from "../components/Categories";
import ShowPlants from "../components/ShowPlants";
import { useEffect } from "react";

//import { useNavigate } from 'react-router-dom';
const Main = () => {
	useEffect(() => {
		const handleScroll = () => {
		  const bottomPanel = document.querySelector('.bottom_panel');
		  if (window.scrollY > 0) {
			bottomPanel.style.display = 'block'; // Show the bottom panel
		  } else {
			bottomPanel.style.display = 'none'; // Hide the bottom panel
		  }
		};

		window.addEventListener('scroll', handleScroll);

		// Cleanup: remove the scroll event listener when the component unmounts
		return () => {
		  window.removeEventListener('scroll', handleScroll);
		};
	  }, []);
return (
	<div className='full_div'>
		<TopNav />
		<div style={{display: 'flex', width:'100%'}}>
			<Categories /> 
			<ShowPlants />
		</div>
			<div className='bottom_panel'></div>
    </div>
);
};

export default Main;
