import React from "react";
import dragonPlant from "../images/plants/dplant1.png";
import monstera from "../images/plants/monstera.png";
import pearl from "../images/plants/pearlstrings.png";
import oak from "../images/plants/oak.png";
import orchid from "../images/plants/orchid.jpeg";
import jasmin from "../images/plants/jasmin.jpg";
import monilaria from "../images/plants/monilaria.webp";
import { useNavigate } from 'react-router-dom';

function ShowPlants() {

  const history = useNavigate();

  function handleClick() {
    history('/plantprofile');
  }
  function checkSeller() {
    history('/sellerprofile');
  }
  function cart() {
    history('/cart');
}
function favourites() {
  history('/favourites');
}

    const plants = [ 
        { name: "Dragon Plant", vendor: "Maria Florista", price : 20, imageUrl: dragonPlant },
        { name: "Monstera",vendor: "Estufas Martins", imageUrl: monstera },
        { name: "Pearl of Strings", vendor: "Continente", imageUrl: pearl },
        { name: "Oak tree", vendor: "José Fernandes", imageUrl: oak },
        { name: "Mini Orchid", vendor: "José Fernandes", imageUrl: orchid },
        { name: "Jasmin", vendor: "João Moura", imageUrl: jasmin },
        { name: "Monilaria", vendor: "Jardim Da Pini", imageUrl: monilaria },
      ]

    return (
        <div style={{flex: 1,border: '2px solid #6B8E23'}}>
            <h2 className="plants-to-show">What we have for you today:</h2>
            <div style={{ display: "grid", gridTemplateColumns: "repeat(4, 1fr)", gap: "1rem" }}>
            {plants.map((plant, index) => (
                <div className = "product"key={index} style={{ border: "1px solid #ccc", padding: "1rem" }}>
            <img src={plant.imageUrl} alt={plant.name} style={{ width: "50%", height: "50%", objectFit: "cover", marginBottom: "0.5rem" }} onClick={handleClick}/>
            <div className = "product-text" style={{ fontStyle: "italic" }}>{plant.name}</div>
            <div className = "product-text"style={{ fontStyle: "italic" }}>Sold by: {plant.vendor}</div>
            <div className = "product-text"style={{ fontStyle: "italic" }}>Price: {plant.price} €</div>
            <div>
              <button className = "product-wrapper" style={{ marginRight: '10px',backgroundColor: '#6B8E23', color: 'white', padding: '5px 20px', fontSize: '16px', borderRadius: '5px', border: 'none', cursor: 'pointer',marginTop:'5px' }}onClick={cart}>Add to cart</button>
              <button className = "product-wrapper"style={{ marginRight: '10px',backgroundColor: '#6B8E23', color: 'white', padding: '5px 20px', fontSize: '16px', borderRadius: '5px', border: 'none', cursor: 'pointer',marginTop:'5px' }} onClick={favourites}>Add to favourites</button>
              <button className = "product-wrapper"style={{ marginRigth: '10px',backgroundColor: '#6B8E23', color: 'white', padding: '5px 20px', fontSize: '16px', borderRadius: '5px', border: 'none', cursor: 'pointer',marginTop:'5px' }} onClick={checkSeller}>Visit seller</button>

            </div>

          </div>
        ))}
      </div>
        </div>
    );
  }

  export default ShowPlants;
