import React, { useState } from "react";
import TopNav from '../components/Navbar/TopNav';
import Categories from "../components/Categories";
import ShowPlants from "../components/ShowPlants";

const Main = () => {

	const [filterPlantName, setFilterPlantName] = useState("");
	const [searchQuery, setSearchQuery] = useState("");
return (
	<div className='full_div'>
		<TopNav />
		<div style={{display: 'flex', width:'100%'}}>
			<Categories setFilterPlantName={setFilterPlantName} />
			<div>
        		<h2 className="plants-to-show" style={{fontFamily: 'Helvetica, sans-serif'}}>Search:</h2>
      		</div>
      		<div style={{ display: 'flex'}}>
      		<input
      		  type="text"
      		  placeholder="Filter plants..."
      		  value={searchQuery}
      		  onChange={(e) => setSearchQuery(e.target.value)}
      		  style={{
      		    backgroundColor: "#ccc",
      		    borderRadius: "20px",
      		    padding: "10px",
      		    border: "none",
      		    width: "300px",
      		    height: "30px",
      		    textAlign: "left",
      		    marginBottom: "10px",
      		    marginTop: "5px",
      		    marginRight: "30px"
      		  }}
      		/>

		  </div>
			<ShowPlants filterPlantName={filterPlantName}/>
		</div>
		<div style={{width: '100%', height: '100px', backgroundColor: '#6B8E23'}}></div>
    </div>
);
};

export default Main;
