import React from "react";
import dragonPlant from "../images/plants/dplant1.png";
import monstera from "../images/plants/monstera.png";
import pearl from "../images/plants/pearlstrings.png";
import oak from "../images/plants/oak.png";
import orchid from "../images/plants/orchid.jpeg";
import jasmin from "../images/plants/jasmin.jpg";
import monilaria from "../images/plants/monilaria.webp";
import { useNavigate } from 'react-router-dom';

const ShowPlants = ({ filterPlantName, filterVendorName }) =>{

  const history = useNavigate();

  function handleClick() {
    history('/plantprofile');
  }
  function checkSeller() {
    history('/sellerprofile');
  }
  function cart() {
    history('/cart');
}
function favourites() {
  history('/favourites');
}


    const plants = [ 
        {storeRating:"3",rating:"4",name: "Dragon Plant", vendor: "Maria Florista", price : 20, imageUrl: dragonPlant },
        {storeRating:"3",rating:"4",name: "Monstera",vendor: "Estufas Martins", imageUrl: monstera },
        {storeRating:"3",rating:"4",name: "Pearl of Strings", vendor: "Continente", imageUrl: pearl },
        {storeRating:"3",rating:"4",name: "Oak tree", vendor: "José Fernandes", imageUrl: oak },
        {storeRating:"3",rating:"4",name: "Mini Orchid", vendor: "José Fernandes", imageUrl: orchid },
        {storeRating:"3",rating:"4",name: "Jasmin", vendor: "João Moura", imageUrl: jasmin },
        {storeRating:"3",rating:"4",name: "Monilaria", vendor: "Jardim Da Pini", imageUrl: monilaria },
      ]

      const filteredPlants = plants.filter((plant) => {
        const matchesName = filterPlantName ? plant.name.toLowerCase().includes(filterPlantName.toLowerCase()) : true;
        const matchesVendor = filterVendorName ? plant.vendor === filterVendorName : true;
        return matchesName && matchesVendor;
      });

      return (

        <div style={{ display: "grid", gridTemplateColumns: "repeat(3, 1fr)", gap: "1rem", padding: '10px' }}>

          {filteredPlants.map((plant, index) => (
            <div className="product" key={index} style={{ border: '2px solid green', borderRadius: '10px', padding: "1rem", backgroundColor: 'rgba(107, 142, 35, 0.3)' }}>
              <img src={plant.imageUrl} alt={plant.name} style={{ width: "50%", height: "50%", objectFit: "cover", marginBottom: "0.5rem" }} onClick={handleClick} />
              <div className="product-text" style={{ fontStyle: "italic", fontSize: '20px' }}>{plant.name}</div>
              <div className="product-text" style={{ fontStyle: "italic", fontSize: '20px' }}>Sold by: {plant.vendor}</div>
              <div className="product-text" style={{ fontStyle: "italic", fontSize: '20px' }}>Price: {plant.price} €</div>
              <center>
                <div style={{ marginTop: '20px' }}>
                  <button className="product-wrapper" style={{ marginRight: '10px', backgroundColor: '#6B8E23', color: 'white', padding: '5px 20px', fontSize: '20px', borderRadius: '5px', border: 'none', cursor: 'pointer', marginTop: '5px' }} onClick={cart}>Add to cart</button>
                  <button className="product-wrapper" style={{ marginRight: '10px', backgroundColor: '#6B8E23', color: 'white', padding: '5px 20px', fontSize: '20px', borderRadius: '5px', border: 'none', cursor: 'pointer', marginTop: '5px' }} onClick={favourites}>Add to favourites</button>
                  <button className="product-wrapper" style={{ marginRight: '10px', backgroundColor: '#6B8E23', color: 'white', padding: '5px 20px', fontSize: '20px', borderRadius: '5px', border: 'none', cursor: 'pointer', marginTop: '5px' }} onClick={checkSeller}>Visit seller</button>
                </div>
              </center>
            </div>
          ))}
        </div>

      );

  }

  export default ShowPlants;
