import React, { useState } from "react";

function Categories() {
  const [showSubcategoriesPlants, setShowSubcategoriesPlants] = useState(false);

  const toggleSubcategoriesPlants = () => {
    setShowSubcategoriesPlants(!showSubcategoriesPlants);
  };
  const [showSubcategoriesTrees, setShowSubcategoriesTrees] = useState(false);

  const toggleSubcategoriesTrees = () => {
    setShowSubcategoriesTrees(!showSubcategoriesTrees);
  };
  return (
    <div style={{ width: "20%", border: "5px solid #6B8E23" }}>
        <h2 className="plants-to-show">Categories:</h2>
        <div>
        <div style={{ display: 'flex', justifyContent: 'flex-end' }}>
			<input 
  			type="text" 
  			placeholder="Search products..."
  			style={{
  			  backgroundColor: '#ccc',
  			  borderRadius: '20px',
  			  padding: '10px',
  			  border: 'none',
  			  width: '200px',
			  height: '50px',
  			  textAlign: 'left',
  			  marginBottom: '10px'	,
			  marginTop:'10px',
			  marginRight: '30px'
  			}}
		/>
		</div>

        <button style={{ border: 'none', paddingLeft: '5', fontWeight: 'bold',marginLeft:10 ,width:'100'}} onClick={toggleSubcategoriesPlants}>
            <h3 style={{ display: 'inline-block', marginRight: '10px' }}>All Plants</h3>
            {showSubcategoriesPlants ? <span>&#x25BC;</span> : <span>&#x25B6;</span>}
        </button>
        {showSubcategoriesPlants && (
            <div style={{ display: 'flex', flexDirection: 'column', paddingLeft: '20px',marginRight:10 }}>
                <button style={{ border: 'none', fontWeight: 'bold',marginTop:1 }}>Indoor Plants</button>
                <button style={{ border: 'none', fontWeight: 'bold',marginTop:1 }}>Outdoor Plants</button>
                <button style={{ border: 'none', fontWeight: 'bold',marginTop:1 }}>Aquatic Plants</button>
            </div>
        )}

        </div>
        <button style={{ border: 'none', paddingLeft: '5', fontWeight: 'bold',marginLeft:10 ,width:'100',marginTop:10}} onClick={toggleSubcategoriesTrees}>
            <h3 style={{ display: 'inline-block', marginRight: '10px' }}>All Trees</h3>
            {showSubcategoriesTrees ? <span>&#x25BC;</span> : <span>&#x25B6;</span>}
        </button>
        {showSubcategoriesTrees && (
            <div style={{ display: 'flex', flexDirection: 'column', paddingLeft: '20px',marginRight:10 }}>
                <button style={{ border: 'none', fontWeight: 'bold',marginTop:1 }}>Bushes</button>
                <button style={{ border: 'none', fontWeight: 'bold',marginTop:1 }}>Trees</button>
                <button style={{ border: 'none', fontWeight: 'bold',marginTop:1 }}>Bonsais</button>
            </div>
        )}
    </div>
  );
}

export default Categories;
