import React from 'react';
import monstera from "../images/plants/monstera.png";
import dragon from "../images/plants/dplant1.png";
import pearl from "../images/plants/pearlstrings.png";
import oak from "../images/plants/image 5.png";
import { useNavigate } from 'react-router-dom';
import { useState } from 'react';




const Cart_product = () => {
    const history = useNavigate();
    const [isFavorite, setIsFavorite] = useState(true); // State to track if it's a favorite
    function removeFromCart() {
        setIsFavorite(false); // Update isFavorite state to false
      }

    if (!isFavorite) {
        return null; // If not a favorite, render nothing (it will disappear)
    } 


    function buy() {
        history('/buy');
      }


    return(
        <div style={{ display: "grid", gridTemplateColumns: "repeat(1, 1fr)", gap: "1rem" ,padding:'10px'}}>

            <div className = 'product_wrapper' >
                <div className= 'product_text' style={{border: '2px solid green', borderRadius: '10px', padding: "1rem" , backgroundColor: 'rgba(107, 142, 35, 0.3)' }}>
                    <div className='cart_product' >
                        <img src={dragon} alt={dragon} style={{ width: 'auto', height: '100%', objectFit: 'cover' }} />
                    </div>
                    <div className='cart_product' >
                        <div style={{ fontStyle: 'italic', marginBottom: '0.5rem' }}>Name: Dragon PLant</div>
                        <div style={{ fontStyle: 'italic', marginBottom: '0.5rem' }}>Sold by: Maria Florista</div>
                        <div className = 'product_text'style={{ fontStyle: 'italic', marginBottom: '0.5rem' }}>Price:</div>
                    </div>
                    <div className='cart_product' >
                        <div style = {{display: 'flex', flexDirection: 'row', justifyContent: 'space-between'}}>
                        <label htmlFor="minPrice">Quantity:</label>
      			        <input type="number" id="minPrice" placeholder="Quantity" style={{ padding: '5px', width: '100px' ,marginTop:'20px'}} />
                        </div>
                        <button style={{ marginRight: '100px', backgroundColor: '#ff6666', color: 'white', padding: '5px 20px', fontSize: '16px', borderRadius: '5px', border: 'none', cursor: 'pointer', marginTop:'20px' } }onClick={removeFromCart} >Remove</button>
                        </div>
                    </div>

            </div>




            </div> 

        );
};

export default Cart_product;
