import React from 'react';
import TopNav from '../components/Navbar/TopNav';
import MiniOrchid from '../components/MiniOrchid';
import MariaFloristaFavourite from '../components/MariaFloristaFavourite';
import Monstera from '../components/Monstera';
import JoseFavourite from '../components/JoseFavourite';


//import {useNavigate} from 'react-router-dom';

const Favourites = () => {

    const userData = localStorage.getItem('users')
    let users = [];
    let plants = [];
    let favPlants = [];
    let favUsers = [];
    if(userData) {
        users = JSON.parse(userData) 
        const user = users.find((user) => user.loggedInUser === true)
        if(user.favPlants.lenth === 0) {

            favPlants = user.favPlants
            for(const plant in user.favPlants) {
                favPlants.push(plant)
            }
        }
    }
    console.log("loggin favourite plants");
    console.log(plants)
    return(
        <div className='full_div2'>
            <TopNav />

            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', width: '80%', margin: '0 auto', marginTop:'50px' }}>
                <div className = "product" style={{ border: '2px solid green', padding: '20px', borderRadius: '10px', boxSizing: 'border-box', width: '45%' }}>
                    <div><h3 style={{fontFamily: 'Open Sans, sans-serif'}}>Your favorite products:</h3></div>
                        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', width: '80%', margin: '0 auto', marginTop:'20px' }}>  
                            <MiniOrchid></MiniOrchid>
                            <Monstera></Monstera>
                         </div>
                </div>
                <div className = "product" style={{ border: '2px solid green', padding: '20px', borderRadius: '10px', boxSizing: 'border-box', width: '45%' }}>
                    <div><h3 style={{fontFamily: 'Open Sans, sans-serif'}}>Your favorite sellers:</h3></div>
                    <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', width: '80%', margin: '0 auto', marginTop:'20px' }}>  

                    <MariaFloristaFavourite></MariaFloristaFavourite>
                    <JoseFavourite></JoseFavourite>
                    </div>
                </div>
            </div>
            <div style={{marginTop:'20%',width: '100%', height: '100px', backgroundColor: '#6B8E23'}}></div>
        </div>
    );
}

export default Favourites;
