import React, { useState } from "react";

const Cart = () => {
  // Define state for the cart items
  const [cartItems, setCartItems] = useState([]);

  // Define a function to add items to the cart
  const addToCart = (item) => {
    setCartItems([...cartItems, item]);
  };

  // Define a function to remove items from the cart
  const removeFromCart = (itemIndex) => {
    const newCartItems = [...cartItems];
    newCartItems.splice(itemIndex, 1);
    setCartItems(newCartItems);
  };

  // Calculate the total price of all items in the cart
  const totalPrice = cartItems.reduce(
    (acc, item) => acc + item.price * item.quantity,
    0
  );

  return (
    <div>
      <h1>Shopping Cart</h1>
      <div>
        {cartItems.length === 0 ? (
          <p>Your cart is empty.</p>
        ) : (
          <ul>
            {cartItems.map((item, index) => (
              <li key={index}>
                {item.name} - {item.price.toFixed(2)} x {item.quantity}{" "}
                <button onClick={() => removeFromCart(index)}>Remove</button>
              </li>
            ))}
          </ul>
        )}
      </div>
      <div>
        <p>Total: {totalPrice.toFixed(2)}</p>
      </div>
      <div>
        <button onClick={() => addToCart({ name: "Product 1", price: 10, quantity: 1 })}>Add Product 1</button>
        <button onClick={() => addToCart({ name: "Product 2", price: 15, quantity: 1 })}>Add Product 2</button>
      </div>
    </div>
  );
};

export default Cart;
